sp_widget.client_script:
api.controller = function($rootScope) {
    /* widget controller */
    var c = this;

    //trigger quiz view on Quizard Viewer widget
    c.selectQuiz = function(quizId) {

        var obj = {
            action: "change_quiz_view",
            quiz_id: quizId
        };

        $rootScope.$broadcast('setQuizView', obj);

    };
};

